const dataDisplay = document.getElementById('data-display');fetch('assets/dummy-data.json').then(response => response.json()).then(data => {data.forEach(item => {const card = document.createElement('div');card.className = 'data-card';card.innerHTML = `<h2>${item.title}</h2><p>${item.description}</p>`;dataDisplay.appendChild(card);});}).catch(error => console.error('Error fetching data:', error));